mapper.select:
SELECT 
FROM orth_job_user AS t


		AND t.username like CONCAT(CONCAT('%', #{username}), '%')


		AND t.role = #{role}


ORDER BY username ASC
LIMIT #{offset}, #{pagesize}

SELECT count(1) FROM orth_job_user AS t
AND t.username like CONCAT(CONCAT('%', #{username}), '%')
AND t.role = #{role}

SELECT 
FROM orth_job_user AS t
WHERE t.username = #{username}

SELECT 
FROM orth_job_user AS t
WHERE t.id = #{id}

mapper.insert:
INSERT INTO orth_job_user (
	username,
	password,
	role,
	permission
) VALUES (
	#{username},
	#{password},
	#{role},
	#{permission}
);

mapper.update:
UPDATE orth_job_user
SET

		password = #{password},

	role = #{role},
	permission = #{permission}
WHERE id = #{id}

UPDATE orth_job_user
SET token = #{token}
WHERE id = #{id}
